Codesri_data &lt;- tibble()

cli_progress_bar(total = length(files), clear = FALSE)

for (i in files) {
  sri_data &lt;-
    i |&gt;
    read_rds() |&gt;
    mutate(
      time =
        time |&gt;
        as.numeric() |&gt;
        divide_by(60) |&gt;
