Codehms_to_decimal_hours &lt;- function(x) {
  rutils:::assert_hms(x)

  x |&gt;
    as.numeric() %&gt;%
    `/`(60 * 60)
}



Codedecimal_hours_to_hms &lt;- function(x) {
  checkmate::assert_numeric(x)

  x %&gt;%
    `*`(60 * 60) |&gt;
    hms::as_hms()
}



Codehms_to_circular &lt;- function(
    x,
    ...,
    type = "angles",
    units = "hours",
    template = "clock24"
) {
  rutils:::assert_hms(x)

  x |&gt;
    hms_to_decimal_hours() |&gt;
    circular::circular(
      type = type,
      units = units,
      template = template,
      ...
    )
}



Codecircular_time_stat &lt;- function(x, fun = circular::mean.circular) {
  rutils:::assert_hms(x)
  checkmate::assert_function(fun)

  if (identical(fun, circular::sd.circular) ||
      identical(fun, stats::sd)) {
    x |&gt;
      hms_to_circular() |&gt;
      fun() |&gt;
      astroFns::rad2hms() |&gt;
      hms::as_hms() |&gt;
      hms::round_hms(secs = 1)
  } else {
    x |&gt;
      hms_to_circular() |&gt;
      fun() |&gt;
      as.numeric() |&gt;
      lubritime::cycle_time(cycle = 24) |&gt;
      decimal_hours_to_hms() |&gt;
      hms::round_hms(secs = 1)
  }
}



Codealt_circular_time_stat &lt;- function(x, fun = mean) {
  rutils:::assert_hms(x)
  checkmate::assert_function(fun)

  x |&gt;
    lubritime:::link_to_timeline(threshold = hms::parse_hms("12:00:00")) |&gt;
    fun() |&gt;
    hms::as_hms() |&gt;
    hms::round_hms(secs = 1)
}



Codelinear_time_stat &lt;- function(x, fun = mean) {
  rutils:::assert_duration(x)
  checkmate::assert_function(fun)

  x |&gt;
    fun() |&gt;
    hms::as_hms() |&gt;
    hms::round_hms(secs = 1)
}



Coderounded_stat &lt;- function(x, fun = mean) {
  checkmate::assert_numeric(x)
  checkmate::assert_function(fun)

  x |&gt;
    fun() |&gt;
    round(3)
}



